package com.example.myapplication;import android.app.AppComponentFactory;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;public class Login extends AppCompatActivity implements View.OnClickListener {    Button bLogin;    EditText etName, etPassword;    TextView tvRegistrationLink;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);        bLogin.findViewById(R.id.bLogin);        etName.findViewById(R.id.etName);        etPassword.findViewById(R.id.etPassword);        tvRegistrationLink.findViewById(R.id.tvRegistrationLink);        bLogin.setOnClickListener(this);        tvRegistrationLink.setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.bLogin:                break;            case R.id.tvRegistrationLink:                startActivity(new Intent(this, Register.class));                break;        }    }}